function getStatus(value, range_values){
    const{very_low_max, low_min, low_max, normal_min, normal_max, high_min, high_max, very_high_min}=range_values;
    if(value <= very_low_max)
        return "very low";
    if(value >= low_min && value <= low_max)
        return "low";
    if(value >= normal_min && value <= normal_max)
        return "normal";
    if(value >= high_min && value <= high_max)
        return "high";
    if(value >= very_high_min)
        return "very_high";
}

